changeSet.sql:
CREATE TABLE user_friends (
    user_id      uuid NOT NULL REFERENCES users (id),
    friend_id    uuid NOT NULL references users (id),
    PRIMARY KEY(user_id, friend_id)
);

COMMENT ON TABLE  user_friends IS 'Друзья';
COMMENT ON COLUMN user_friends.user_id IS 'id пользователя';
COMMENT ON COLUMN user_friends.friend_id IS 'id друга';
